// src/router/index.js
import { createRouter, createWebHistory } from 'vue-router';

const routes = [
  {
    path: '/admin/',
    name: 'Dashboard',
    component: () => import('@/views/admin/DashboardView.vue'),
    meta: {
      // permissions: ['view_dashboard'],
      requiresAuth: true,
      title: 'Dashboard',
      icon: 'mdi-view-dashboard',
      breadcrumb: [{ text: 'Dashboard' }]
    }
  },
  {
    path: '/admin/categories',
    name: 'Categories',
    component: () => import('@/views/admin/category/CategoryList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Danh mục',
      permissions: ['view_categories'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Danh mục' }
      ]
    }
  },
  {
    path: '/admin/products',
    name: 'Products',
    component: () => import('@/views/admin/product/ProductList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Sản phẩm',
      permissions: ['manage_products'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Sản phẩm' }
      ]
    }
  },
  {
    path: '/admin/products/create',
    name: 'ProductCreate',
    component: () => import('@/views/admin/product/ProductForm.vue'),
    meta: {
      requiresAuth: true,
      title: 'Thêm sản phẩm mới',
      permissions: ['manage_products'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Sản phẩm', to: '/products' },
        { text: 'Thêm mới' }
      ]
    }
  },
  {
    path: '/admin/products/:id/edit',
    name: 'ProductEdit',
    component: () => import('@/views/admin/product/ProductForm.vue'),
    meta: {
      requiresAuth: true,
      title: 'Chỉnh sửa sản phẩm',
      permissions: ['manage_products'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Sản phẩm', to: '/products' },
        { text: 'Chỉnh sửa' }
      ]
    },
    props: true
  },
  {
    path: '/admin/orders',
    name: 'Orders',
    component: () => import('@/views/admin/order/OrderList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Đơn hàng',
      permissions: ['manage_orders'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Đơn hàng' }
      ]
    }
  },
  {
    path: '/admin/orders/:id',
    name: 'OrderDetail',
    component: () => import('@/views/admin/order/OrderDetail.vue'),
    meta: {
      requiresAuth: true,
      title: 'Chi tiết đơn hàng',
      permissions: ['manage_orders'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Đơn hàng', to: '/orders' },
        { text: 'Chi tiết' }
      ]
    },
    props: true
  },
  {
    path: '/admin/customers',
    name: 'Customers',
    component: () => import('@/views/admin/customer/CustomerList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Khách hàng',
      permissions: ['manage_users'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Khách hàng' }
      ]
    }
  },
  {
    path: '/admin/vouchers',
    name: 'Vouchers',
    component: () => import('@/views/admin/voucher/VoucherList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Mã giảm giá',
      permissions: ['manage_vouchers'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Mã giảm giá' }
      ]
    }
  },
  {
    path: '/admin/news',
    name: 'News',
    component: () => import('@/views/admin/news/NewsList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Tin tức',
      permissions: ['manage_news'],
      breadcrumb: [
        { text: 'Dashboard', to: '/admin/' },
        { text: 'Tin tức' }
      ]
    }
  },
  {
    path: '/admin/favorite-products',
    name: 'FavoriteProducts',
    component: () => import('@/views/admin/product/FavoriteProductList.vue'),
    meta: {
      permissions: ['manage_products'],
      requiresAuth: true,
      title: 'Sản phẩm yêu thích',
      breadcrumb: [
        { text: 'Dashboard', to: '/admin/' },
        { text: 'Sản phẩm yêu thích' }
      ]
    }
  },
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/views/user/auth/LoginView.vue'),
    meta: {
      requiresGuest: true,
      title: 'Đăng nhập'
    }
  },
  {
    path: '/logout',
    name: 'Logout',
    component: () => import('@/views/user/auth/LogoutView.vue'),
    meta: {
      requiresAuth: true
    }
  },
    {
    path: '/forbidden',
    name: 'Forbidden',
    component: () => import('@/views/errors/Forbidden.vue'), // Tạo file này nếu chưa có
    meta: { title: 'Truy cập bị từ chối' }
  }
  // {
  //   path: '/:pathMatch(.*)*',
  //   name: 'NotFound',
  //   component: () => import('../views/user/errors/NotFound.vue'),
  //   meta: {
  //     title: 'Không tìm thấy trang'
  //   }
  // }
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
  scrollBehavior(to, from, savedPosition) {
    return savedPosition || { top: 0, behavior: 'smooth' };
  }
});

router.beforeEach(async (to, from, next) => {
  // 1. Xử lý tiêu đề trang
  document.title = to.meta.title ? `${to.meta.title} | Hệ thống Quản lý` : 'Hệ thống Quản lý';
  
  // 2. Lấy thông tin người dùng
  const authToken = localStorage.getItem('authToken');
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const permissions = user.permissions || [];
  const isAdmin = user.Role === 1; // Giả sử Role 1 là admin

  console.log(`Navigation: ${from.path} -> ${to.path}`);
  console.log('Auth status:', !!authToken);
  console.log('User role:', user.Role);

  // 3. Route yêu cầu auth nhưng chưa đăng nhập
  if (to.meta.requiresAuth && !authToken) {
    console.log('Redirect to login - requires auth');
    return next({ 
      name: 'Login', 
      query: { redirect: to.fullPath } 
    });
  }

  // 4. Route yêu cầu guest nhưng đã đăng nhập
  if (to.meta.requiresGuest && authToken) {
    console.log('Redirect to dashboard - already authenticated');
    return next({ 
      name: 'Dashboard'
    });
  }

  // 5. Kiểm tra quyền truy cập admin - THAY ĐỔI CHÍNH Ở ĐÂY
 // Sửa thành (kiểm tra kỹ hơn):
if (to.path.startsWith('/admin')) {
  console.log('Admin route access check:', {
    hasToken: !!authToken,
    userRole: user.Role,
    isAdmin: isAdmin
  });

  if (!isAdmin) {
    console.warn('Access denied - Admin only');
    if (authToken) {
      return next('/forbidden');
    } else {
      return next({ 
        name: 'Login', 
        query: { redirect: to.fullPath, admin_required: '1' } 
      });
    }
  }
}

  // 6. Kiểm tra permissions (nếu route yêu cầu)
  if (to.meta.permissions && !isAdmin) {
    const hasPermission = to.meta.permissions.some(p => 
      permissions.includes(p) || 
      permissions.includes('*')
    );
    
    if (!hasPermission) {
      console.warn('Permission denied for route:', to.path);
      return next('/forbidden');
    }
  }

  next();
});

export default router;
