// src/router/index.js
import { createRouter, createWebHistory } from 'vue-router';

const routes = [
  {
    path: '/admin/',
    name: 'Dashboard',
    component: () => import('@/views/admin/DashboardView.vue'),
    meta: {
      permissions: ['view_dashboard'],
      requiresAuth: true,
      title: 'Dashboard',
      icon: 'mdi-view-dashboard',
      breadcrumb: [{ text: 'Dashboard' }]
    }
  },
  {
    path: '/admin/categories',
    name: 'Categories',
    component: () => import('@/views/admin/category/CategoryList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Danh mục',
      permissions: ['view_categories'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Danh mục' }
      ]
    }
  },
  {
    path: '/admin/products',
    name: 'Products',
    component: () => import('@/views/admin/product/ProductList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Sản phẩm',
      permissions: ['manage_products'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Sản phẩm' }
      ]
    }
  },
  {
    path: '/admin/products/create',
    name: 'ProductCreate',
    component: () => import('@/views/admin/product/ProductForm.vue'),
    meta: {
      requiresAuth: true,
      title: 'Thêm sản phẩm mới',
      permissions: ['manage_products'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Sản phẩm', to: '/products' },
        { text: 'Thêm mới' }
      ]
    }
  },
  {
    path: '/admin/products/:id/edit',
    name: 'ProductEdit',
    component: () => import('@/views/admin/product/ProductForm.vue'),
    meta: {
      requiresAuth: true,
      title: 'Chỉnh sửa sản phẩm',
      permissions: ['manage_products'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Sản phẩm', to: '/products' },
        { text: 'Chỉnh sửa' }
      ]
    },
    props: true
  },
  {
    path: '/admin/orders',
    name: 'Orders',
    component: () => import('@/views/admin/order/OrderList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Đơn hàng',
      permissions: ['manage_orders'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Đơn hàng' }
      ]
    }
  },
  {
    path: '/admin/orders/:id',
    name: 'OrderDetail',
    component: () => import('@/views/admin/order/OrderDetail.vue'),
    meta: {
      requiresAuth: true,
      title: 'Chi tiết đơn hàng',
      permissions: ['manage_orders'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Đơn hàng', to: '/orders' },
        { text: 'Chi tiết' }
      ]
    },
    props: true
  },
  {
    path: '/admin/customers',
    name: 'Customers',
    component: () => import('@/views/admin/customer/CustomerList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Khách hàng',
      permissions: ['manage_users'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Khách hàng' }
      ]
    }
  },
  {
    path: '/admin/vouchers',
    name: 'Vouchers',
    component: () => import('@/views/admin/voucher/VoucherList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Mã giảm giá',
      permissions: ['manage_vouchers'],
      breadcrumb: [
        { text: 'Dashboard', to: '/' },
        { text: 'Mã giảm giá' }
      ]
    }
  },
  {
    path: '/admin/news',
    name: 'News',
    component: () => import('@/views/admin/news/NewsList.vue'),
    meta: {
      requiresAuth: true,
      title: 'Quản lý Tin tức',
      permissions: ['manage_news'],
      breadcrumb: [
        { text: 'Dashboard', to: '/admin/' },
        { text: 'Tin tức' }
      ]
    }
  },
  {
    path: '/admin/favorite-products',
    name: 'FavoriteProducts',
    component: () => import('@/views/admin/product/FavoriteProductList.vue'),
    meta: {
      permissions: ['manage_products'],
      requiresAuth: true,
      title: 'Sản phẩm yêu thích',
      breadcrumb: [
        { text: 'Dashboard', to: '/admin/' },
        { text: 'Sản phẩm yêu thích' }
      ]
    }
  },
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/views/user/auth/LoginView.vue'),
    meta: {
      requiresGuest: true,
      title: 'Đăng nhập'
    }
  },
  {
    path: '/logout',
    name: 'Logout',
    component: () => import('@/views/user/auth/LogoutView.vue'),
    meta: {
      requiresAuth: true
    }
  }
  // {
  //   path: '/:pathMatch(.*)*',
  //   name: 'NotFound',
  //   component: () => import('../views/user/errors/NotFound.vue'),
  //   meta: {
  //     title: 'Không tìm thấy trang'
  //   }
  // }
];

const router = createRouter({
history: createWebHistory(process.env.BASE_URL),
routes,
  scrollBehavior(to, from, savedPosition) {
    if (savedPosition) {
      return savedPosition;
    } else {
      return { top: 0, behavior: 'smooth' };
    }
  }
});
router.beforeEach((to, from, next) => {
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const permissions = user.permissions || [];
  
  console.log('Current route:', to.path);
  console.log('Required permissions:', to.meta.permissions);
  console.log('User permissions:', permissions);

  // Kiểm tra permissions
  if (to.meta.permissions) {
    const hasPermission = to.meta.permissions.some(p => 
      permissions.includes(p) || permissions.includes('*')
    );
    
    if (!hasPermission) {
      console.warn('Permission denied - Missing:', to.meta.permissions);
      return next({ name: 'NotFound' });
    }
  }
  
  next();
});
// Xử lý tiêu đề trang
router.beforeEach((to, from, next) => {
  document.title = to.meta.title ? `${to.meta.title} | Hệ thống Quản lý` : 'Hệ thống Quản lý';
  next();
});

// Route guard kiểm tra đăng nhập và phân quyền
router.beforeEach(async (to, from, next) => {
  const isAuthenticated = localStorage.getItem('authToken');
  const userPermissions = JSON.parse(localStorage.getItem('userPermissions') || '[]');

  // Route yêu cầu đăng nhập nhưng chưa đăng nhập
  if (to.meta.requiresAuth && !isAuthenticated) {
    next({ name: 'Login', query: { redirect: to.fullPath } });
  } 
  // Route yêu cầu chưa đăng nhập nhưng đã đăng nhập
  else if (to.meta.requiresGuest && isAuthenticated) {
    next({ name: 'Dashboard' });
  }
  // Kiểm tra quyền truy cập
  else if (to.meta.permissions) {
    const hasPermission = to.meta.permissions.some(permission => 
      userPermissions.includes(permission)
    );
    
    if (!hasPermission) {
      next({ name: 'NotFound' });
    } else {
      next();
    }
  } else {
    next();
  }
});

export default router;
