import { createRouter, createWebHistory } from 'vue-router';

const routes = [
  {
    path: '/',
    name: 'Dashboard',
    component: () => import('../components/DashboardPage.vue'),
    meta: { requiresAuth: true, title: 'Dashboard' }
  },
  {
    path: '/categories',
    name: 'Categories',
    component: () => import('../components/CategoryPage.vue'),
    meta: { requiresAuth: true, title: 'Categories' }
  },
  {
    path: '/products',
    name: 'Products',
    component: () => import('../components/ProductPage.vue'),
    meta: { requiresAuth: true, title: 'Products' }
  },
  // ... các route khác tương tự
  {
    path: '/logout',
    name: 'Logout',
    component: () => import('../components/LogoutPage.vue'),
    meta: { requiresAuth: true }
  }
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
  scrollBehavior(to, from, savedPosition) {
    return savedPosition || { top: 0 };
  }
});

router.beforeEach((to, from, next) => {
  document.title = to.meta.title ? `${to.meta.title} | My App` : 'My App';
  
  const isAuthenticated = localStorage.getItem('authToken');
  if (to.meta.requiresAuth && !isAuthenticated) {
    next('/login');
  } else {
    next();
  }
});

export default router;
